
const { Recognizer } = require('../../lib');
const recognizer = new Recognizer();

async function correr() {
  recognizer.nlpManager.addLanguage('es');
  recognizer.nlpManager.addDocument(
    'es',
    'Si me encantar√≠a',
    'bot-buy-disfraces-yes'
  );
  recognizer.nlpManager.addDocument(
    'es',
    'No por ahora no quiero nada',
    'bot-buy-disfraces-no'
  );

  await recognizer.nlpManager.train();
  const result = await recognizer.process({}, 'es', 'si me encatar');
  console.log(result);
}
correr()