const fs = require('fs');
const meh = require('./meh.json');
const meh2 = require('./meh2.json');

function process(a, b) {
  const result = [];
  for (let i = 0; i < a.length; i += 1) {
    result.push({
      utterance: a[i].utterance,
      expected: a[i].expected,
      actualNeu: a[i].actualNeu,
      actualLRC: b[i].actualLRC,
      classificationNeu: a[i].classificationNeu,
      classificationLRC: b[i].classificationLRC,
    });
  }
  return result;
}

const result = {
  chatbot: process(meh.chatbot, meh2.chatbot),
  askUbuntu: process(meh.askUbuntu, meh2.askUbuntu),
  webApplication: process(meh.webApplication, meh2.webApplication),
};

fs.writeFileSync('./comparative.json', JSON.stringify(result, null, 2), 'utf8');
